import React, { Component } from "react";
import axios from "axios";
import "./Dashboard.css";
import MyTable from "./MyTable";

import { Link, Redirect } from "react-router-dom";

export default class Dashboard extends Component {
  constructor(props) {
    // if (localStorage.setItem("token", this.state.loginParams.user_id)) {
    // }
    super(props);
    this.state = {
      memeResponse: [],
      islogout: false,
      checkedList: [],
      modal: false,
      searchTerm: "",
      currImg: "",
      memeResponse: [],
      showList: [],
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleCheck = this.handleCheck.bind(this);
    this.handleSearch = this.handleSearch.bind(this);
    // this.handleShowList = this.handleShowList.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.signOut = this.signOut.bind(this);
  }

  componentDidMount() {
    const url = "https://api.imgflip.com/get_memes";
    axios.get(url).then((response) => {
      //console.log('hi', response.data.data)
      this.setState(
        {
          memeResponse: response.data.data.memes.map((a) => {
            return {
              ...a,
              isChecked: false,
            };
          }),
        },
        () => {
          this.setState({
            showList: this.state.memeResponse,
          });
        }
      );
    });

    console.log("heheheh");
  }

  handleChange(event) {
    console.log(event.target.value);
  }

  signOut() {
    this.setState({
      islogout: true,
    });
    localStorage.removeItem("token");
    localStorage.removeItem("username");
  }

  render() {
    //Checklist there, on
    if (!localStorage.getItem("token")) {
      return <Redirect to="/" />;
    } else if (this.state.islogout) {
      return <Redirect to="/" />;
    }
    return (
      <div className="dashboard grid-container col-3">
        <div className="grid-content">
          <ul className="list">
            <div className="list-heading">
              <span>Hello User</span>
              <i className="fa-solid fa-minus"></i>
            </div>
            <li>Create a List</li>
            <li>SHow My List</li>
            <li>
              <button className="btn-secd">Logout</button>
            </li>
          </ul>
        </div>
        <div className="grid-content">
          <div className="input-field">
            <input id="email-field" type="text" pattern=".*\S.*" required />
          </div>
          <div className="flex">
            <div className="card card-horizontal">
              <div className="content-horizontal">
                <img
                  className="card-img"
                  src="https://picsum.photos/500"
                  alt="loading"
                />

                <div className="card-text">
                  <p>
                    Visit ten places on our planet that are undergoing the
                    biggest changes today.
                  </p>
                </div>
              </div>
              <input type="checkbox" name="" id="" />
            </div>
          </div>
        </div>
        <div className="grid-content">
          <button className="btn-prim">Submit</button>
        </div>
      </div>
    );
  }
}
